@extends('layouts.member')@section('mcss')    <link rel="stylesheet" href="/style/css/new/base.css">    <link rel="stylesheet" href="/style/css/new/order.css">    <link rel="stylesheet" href="/style/css/pagination.css">    <script src="/style/js_source/jquery-1.9.0.js"></script>    <script type="text/javascript" src="/style/js_source/jquery.blockui.js"></script>    <script src="/style/common/js/constant.js"></script>    <script src="/style/jqpage/js/jquery.pagination.js"></script>    <link rel="stylesheet" href="/style/css/bootstrap.css">    <link rel="stylesheet" href="/style/css/multiple-select.css">    <link rel="stylesheet" href="/style/css/myinfo.css">    <script>        $(function(){            //分页div生成5,90            var maxPage ="5";            var pcount = "90";            var current_page="1";            $('.pagination').pagination(pcount, {                callback : pageselectCallback,// PageCallback() 为翻页调用次函数。                prev_text : " 上一页",                next_text : "下一页 ",                current_page: current_page-1,                items_per_page : 20, // 每页的数据个数                num_display_entries : 3, // 两侧首尾分页条目数                num_edge_entries : 2                // 连续分页主体部分分页条目数            });            function pageselectCallback(page_id, jq) {                var pageInfo = page_id + 1;                $("#index").val(pageInfo);                $("#index").val                $("#inputForm").submit();            }        })    </script>    <script>        var _payStatus = "";        var _pay_terms = "";        var _prodType = "";        var _prodBrand = "";        var orderId = "";        var startTime = "";        var endTime = "";        var orderType = "";        var orderStatus = "";        var parentMultiId = "";        $(document).ready(function() {            function loadXML(xmlString){                var xmlDoc=null;                //判断浏览器的类型                //支持IE浏览器                if(!window.DOMParser && window.ActiveXObject){   //window.DOMParser 判断是否是非ie浏览器                    var xmlDomVersions = ['MSXML.2.DOMDocument.6.0','MSXML.2.DOMDocument.3.0','Microsoft.XMLDOM'];                    for(var i=0;i<xmlDomVersions.length;i++){                        try{                            xmlDoc = new ActiveXObject(xmlDomVersions[i]);                            xmlDoc.async = false;                            xmlDoc.loadXML(xmlString); //loadXML方法载入xml字符串                            break;                        }catch(e){                        }                    }                }                //支持Mozilla浏览器                else if(window.DOMParser && document.implementation && document.implementation.createDocument){                    try{                        /* DOMParser 对象解析 XML 文本并返回一个 XML Document 对象。                         * 要使用 DOMParser，使用不带参数的构造函数来实例化它，然后调用其 parseFromString() 方法                         * parseFromString(text, contentType) 参数text:要解析的 XML 标记 参数contentType文本的内容类型                         * 可能是 "text/xml" 、"application/xml" 或 "application/xhtml+xml" 中的一个。注意，不支持 "text/html"。                         */                        domParser = new  DOMParser();                        xmlDoc = domParser.parseFromString(xmlString, 'text/xml');                    }catch(e){                    }                }                else{                    return null;                }                return xmlDoc;            }            var xmlHttp;            if(window.ActiveXObject){                $.ajax({                    url:'DictionaryValueAction!getProductInfo.shtml',                    type: 'post',                    timeout: 10000,                    error: function()                    {                    },                    success: function(data)                    {                        //areaDoc = loadXML(data)                        areaDoc = new ActiveXObject("Microsoft.XMLDOM");                        areaDoc.async = false;                        areaDoc.loadXML(data);                        var provinces = areaDoc.getElementsByTagName("ProductType");                        //省的SELECT                        for (var i = 0; i < provinces.length; i++) {                            var id = provinces[i].getAttribute("ID");                            var name = provinces[i].getAttribute("NAME");                            $('#prodType').append("<option value='"+id+"'>"+name+"</option>");                        }                        if(_prodType!="")                        {                            $('#prodType').val(_prodType);                            productTypeChange();                            $('#prodBrand').val(_prodBrand);                        }                    }                });            }else{                xmlHttp = new XMLHttpRequest();                xmlHttp.open("GET","DictionaryValueAction!getProductInfo.shtml");                xmlHttp.onreadystatechange = callback;                xmlHttp.send(null);                function callback(){                    if(xmlHttp.readyState == 4){                        if(xmlHttp.status==200){                            areaDoc = xmlHttp.response;                            areaDoc = loadXML(areaDoc);                            provinces = areaDoc.getElementsByTagName("ProductType");                            //省的SELECT                            for (var i = 0; i < provinces.length; i++) {                                var name = provinces[i].getAttribute("NAME");                                var id = provinces[i].getAttribute("ID");                                $('#prodType').append("<option value='"+id+"'>"+name+"</option>");                            }                            if(_prodType!="")                            {                                $('#prodType').val(_prodType);                                productTypeChange();                                $('#prodBrand').val(_prodBrand);                            }                        }                    }                }            }            $.ajax({                type: "GET",                url: domain + '/member!getUserChildAccount.shtml',                contentType: "text/plain; charset=utf-8",                dataType : 'json',                xhrFields: {                    withCredentials: true                },                success: function(data){                    console.log(data);                    console.log(data.parentMultiId);                    if(data.childAccountValue.length == 0){                        $('#childAccountMs').css('display', 'none');                        $('#msName').css('display', 'none');                        if(data.parentMultiId && data.parentMultiId != null){                            parentMultiId = data.parentMultiId;                        }                    }else{                        $('#childAccountMs').css('display', 'block');                        $('#msName').css('display', 'block');                        $('#childAccountMs').empty();                        for(var index = 0;index < data.childAccountValue.length; index++){                            $('#childAccountMs').append('<option value='+data.childAccountValue[index].cust_id+'>'+data.childAccountValue[index].cust_name+'</option>');                        }                        $('#childAccountMs').multipleSelect();                    }                },                error: function()                {                }            });            $("#excelExport").click(function(){                if(parentMultiId != ''){                    alert("对不起,您没有导出权限!");                }else{                    var orderId = $("#orderId").val();                    var orderEndtime = $("#orderEndtime").val();                    var orderStarttime = $("#orderStarttime").val();                    var childAccount = $("#childAccountMs").val();                    var payStatus = $("#payStatus").val();                    var payTerms = $("#pay_terms").val();                    var prodType = $("#prodType").val();                    var prodBrand = $("#prodBrand").val();                    var orderType = $("#orderType").val();                    var orderStatus = $("#orderStatus").val();                    window.location.href=domain + "/OrderAction!summaryOfConsumptionExport.shtml?orderId="+orderId+"&orderEndtime="+orderEndtime+"&orderStarttime=" +orderStarttime+"&childAccount="+childAccount                        +"&payStatus="+payStatus+"&payTerms="+payTerms+"&prodType="+prodType+"&prodBrand="+prodBrand+"&orderType="+orderType+"&orderStatus="+orderStatus;                }            });        });        var brandList;        function productTypeChange(){            var prodType = $('#prodType').val();            //清空城市和区县的数据            $('#prodBrand').empty();            //追加城市的列表            if(prodType != ""){                $('#prodBrand').prepend("<option value=''>全部</option>");                //var cities = areaDoc.selectNodes("//Province[@NAME='"+province+"']").nextNode().childNodes;                if(window.ActiveXObject){                    brandList = areaDoc.selectNodes("//ProductType[@ID='"+prodType+"']").nextNode().childNodes;                }else{                    brandList = provinces[document.getElementById("prodType").selectedIndex-1].getElementsByTagName("Brand") ;                }                //市的SELECT                for (var i = 0; i < brandList.length; i++) {                    var id = brandList[i].getAttribute("ID");                    var name = brandList[i].getAttribute("NAME");                    $('#prodBrand').append("<option value='"+id+"'>"+name+"</option>");                }            }            else            {                $('#prodBrand').prepend("<option value=''>全部</option>");            }        }    </script>@stop@section('mianbaoxie')    <a href="/">个人信息管理</a>><a href="member Java script：">我的资料</a>@stop@section('right')    <div class="price_head fn-clear">        <div class="fn-left info_logo">            <i></i>我的资料        </div>    </div>    <table width="90%" border="0" align="center" cellpadding="0" cellspacing="0" class="biaoge2 myinfo">        <tr>            <td colspan="2" style="width:100%">请您真实填写以下信息，以便我们可以及时联系到您，找眼镜网尊重客户隐私，不会把您的信息透露给第三方。<br></td>        </tr>        <tr></tr>        <tr>            <td  style="width:10%" align="right" bgcolor="#f0f0f0">登录账号：</td>            <td style="width:78%">{{$person->username}}</td>        </tr>        <tr>            <td  style="width:10%" align="right" bgcolor="#f0f0f0">公司名称：</td>            <td style="width:78%">{{$person->name}}</td>        </tr>        <tr>            <td  style="width:10%" align="right" bgcolor="#f0f0f0">手机：</td>            <td  style="width:78%">{{$person->z_3}}</td>        </tr>        <tr>            <td  style="width:10%"  align="right" bgcolor="#f0f0f0">城市：</td>            <td  style="width:78%">{{$person->z_1}}-{{$person->z_11}}-{{$person->z_12}}-{{$person->z_13}}</td>        </tr>        <tr>            <td  style="width:10%"  align="right" bgcolor="#f0f0f0">固定电话：</td>            <td  style="width:78%">{{$person->z_5}}</td>        </tr>        <tr>            <td  style="width:10%"  align="right" bgcolor="#f0f0f0">联系人：</td>            <td  style="width:78%">{{$person->z_4}}</td>        </tr>        <tr>            <td  style="width:10%"  align="right" bgcolor="#f0f0f0">邮箱：</td>            <td  style="width:78%">{{$person->z_6}}</td>        </tr>    </table>@stop@section('scripts')    <script src="/style/js/multiple-select.js"></script>    <script src="/style/js_new/lhgcore.min.js"></script>    <script src="/style/js_new/lhgcalendar.min.js"></script>    <script>        $('.oldData').click(function(){            alert("如需对订单进行相关操作，请联系客服");        });        function initSelect()        {            if(orderId != "")            {                $("#orderId").val(orderId);            }            if(startTime != "")            {                $("#orderStarttime").val(startTime);            }            if(endTime != "")            {                $("#orderEndtime").val(endTime);            }            if(_payStatus != "")            {                $('#payStatus').val(_payStatus);            }            if(_pay_terms != "")            {                $('#pay_terms').val(_pay_terms);            }            if(_prodType != "")            {                $("#_prodType").val(_prodType);            }            if(_prodBrand != "")            {                $("#prodBrand").val(_prodBrand);            }            if(startTime != "")            {                $('.orderStarttime').val(startTime);            }            if(endTime != "")            {                $('.orderEndtime').val(endTime);            }            if(orderType != "")            {                $('#orderType').val(orderType);            }            if(orderStatus != "")            {                $('#orderStatus').val(orderStatus);            }        }        initSelect();        J(function(){            J('#orderStarttime').calendar({ maxDate:'#orderEndtime' });            J('#orderEndtime').calendar({ minDate:'#orderStarttime' });        });    </script>@stop